def get_config():
    config = MainConfig('cranberrypy.ini')
    cwd = os.getcwd()
    if 'tests' in cwd:
        config.project_path = cwd.replace('tests', 'examples/')
    else:
        config.project_path = cwd + '/examples/'
    config.save_dir = cwd.replace('tests', 'temp/')
    return config
